import React from "react";
import { Hammer, Ruler, Layers, Wrench } from "lucide-react";

const services = [
  {
    icon: <Hammer size={32} className="text-[#ffc107]" />,
    title: "Custom Iron Fabrication",
    description: "Expertly crafted ironworks tailored to your specific needs, from gates to furniture.",
  },
  {
    icon: <Ruler size={32} className="text-[#ffc107]" />,
    title: "Structural Iron Design",
    description: "High-quality structural designs ensuring safety and durability for all projects.",
  },
  {
    icon: <Layers size={32} className="text-[#ffc107]" />,
    title: "Metal Restoration",
    description: "Restore and revitalize your ironworks to their original charm and functionality.",
  },
  {
    icon: <Wrench size={32} className="text-[#ffc107]" />,
    title: "On-site Repairs",
    description: "Reliable on-site repairs for all types of iron and metal structures.",
  },
];

const ServicePage = () => {
  return (
    <div className="min-h-screen bg-gradient-to-r from-gray-50 to-gray-100 py-12 px-6">
      <div className="max-w-7xl mx-auto">
        <header className="text-center mb-16">
          <h1 className="text-5xl font-extrabold text-gray-800 mb-6">Our Services</h1>
          <p className="text-xl text-gray-600 leading-relaxed">
            Discover premium ironworks services for construction, design, and restoration, crafted with precision and care.
          </p>
        </header>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-10">
          {services.map((service, index) => (
            <div
              key={index}
              className="shadow-xl rounded-xl hover:shadow-2xl hover:-translate-y-1 transform transition-all bg-white border border-gray-200 p-8 text-center"
            >
              <div className="mb-6">{service.icon}</div>
              <h2 className="text-2xl font-bold text-gray-800 mb-4">{service.title}</h2>
              <p className="text-gray-600 leading-relaxed mb-6">{service.description}</p>
              <button className="bg-[#ffc107] text-white px-8 py-3 rounded-full hover:bg-[#e6ac06]">
                Learn More
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ServicePage;
