import React, { useState, useEffect } from "react";
import axios from "axios";

const ImageUpload = () => {
  const [images, setImages] = useState([]);
  const [file, setFile] = useState(null);

  // Fetch images
  useEffect(() => {
    axios.get("http://localhost:3001/api/images").then((res) => setImages(res.data));
  }, []);

  // Upload image
  const handleUpload = async () => {
    const formData = new FormData();
    formData.append("image", file);

    await axios.post("http://localhost:3001/api/images/upload", formData, {
      headers: { "Content-Type": "multipart/form-data" },
    });

    // Reload images
    const res = await axios.get("http://localhost:3001/api/images");
    setImages(res.data);
  };

  return (
    <div>
      <h1>Image Upload</h1>
      <input type="file" onChange={(e) => setFile(e.target.files[0])} />
      <button onClick={handleUpload}>Upload</button>

      <h2>Uploaded Images</h2>
      <div>
        {images.map((img) => (
          <div key={img._id}>
            <img
              src={`http://localhost:3001/api/images/image/${img.filename}`}
              alt={img.filename}
              style={{ width: "200px", height: "200px" }}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default ImageUpload;
