import React from "react";
import { motion } from "framer-motion";
import '../components/Header.jsx';

const AboutUs = () => {
  return (
    <div className="about-us-page" style={styles.container}>
      {/* About Section */}
      <section style={styles.aboutSection}>
        <div style={styles.aboutContent}>
          {/* Image */}
          <motion.div
            style={styles.imageContainer}
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1 }}
          >
            <img
              src="/images/2.jpg" // Replace with construction-related image
              alt="Ceylone Engineering Team"
              style={styles.image}
            />
          </motion.div>
          {/* Text Content */}
          <motion.div
            style={styles.textContent}
            initial={{ x: -100, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ duration: 1 }}
          >
            <h2 style={styles.heading}>Who We Are</h2>
            <p style={styles.paragraph}>
              At Ceylone Engineering, weâ€™ve been crafting top-notch ironworks for
              over 15 years. From custom gates to intricate railings, we combine
              tradition, technology, and passion to deliver excellence. Our mission
              is to enhance every space with durable, elegant, and bespoke iron
              creations.
            </p>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

const styles = {
  container: {
    fontFamily: "'Roboto', sans-serif",
    color: "#333",
    lineHeight: "1.6",
    backgroundColor: "#f7f7f7", // Light background for contrast
  },
  aboutSection: {
    padding: "40px 20px",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#e2e2e2", // Subtle gray background for the section
    borderRadius: "10px",
    boxShadow: "0 8px 16px rgba(0, 0, 0, 0.1)",
  },
  aboutContent: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    maxWidth: "1200px",
    width: "100%",
    gap: "20px",
  },
  imageContainer: {
    flex: 1,
    display: "flex",
    justifyContent: "center",
  },
  textContent: {
    flex: 1,
    textAlign: "left",
  },
  image: {
    width: "60%",
    maxWidth: "500px",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
  },
  heading: {
    fontSize: "2.5rem",
    fontWeight: "bold", // Stronger font for headings
    marginBottom: "1rem",
    color: "#f0a500", // Bright yellow to reflect construction theme
  },
  paragraph: {
    fontSize: "1.1rem",
    marginBottom: "1.5rem",
    color: "#444", // Darker text for better readability
  },
};

export default AboutUs;
