import React from "react";
import { motion } from "framer-motion";
import '../components/Header.jsx';

const AboutUs = () => {
  // Animation Variants
  const fadeInVariant = {
    hidden: { opacity: 0 },
    visible: { opacity: 1, transition: { duration: 1.2, ease: "easeInOut" } },
  };

  const staggerContainer = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
        delayChildren: 0.3,
      },
    },
  };

  return (
    <div className="about-us-page" style={styles.container}>
      {/* About Section */}
      <motion.section
        style={styles.aboutSection}
        variants={fadeInVariant}
        initial="hidden"
        animate="visible"
      >
        <motion.div
          style={styles.aboutContent}
          variants={staggerContainer}
          initial="hidden"
          animate="visible"
        >
          {/* Image */}
          <motion.div
            style={styles.imageContainer}
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{
              duration: 1.5,
              ease: "easeInOut",
            }}
          >
            <div style={styles.imageWrapper}>
              <img
                src="/images/1.jpg" // Replace with a high-quality image
                alt="Ceylone Engineering Team"
                style={styles.image}
              />
            </div>
          </motion.div>
          {/* Text Content */}
          <motion.div
            style={styles.textContent}
            variants={fadeInVariant}
            initial="hidden"
            animate="visible"
          >
            <h2 style={styles.heading}>Who We Are</h2>
            <p style={styles.paragraph}>
              At Ceylone Engineering, we’ve been crafting top-notch ironworks for
              over 15 years. From custom gates to intricate railings, we combine
              tradition, technology, and passion to deliver excellence. Our mission
              is to enhance every space with durable, elegant, and bespoke iron
              creations.
            </p>
          </motion.div>
        </motion.div>
      </motion.section>

      {/* Why Choose Us Section */}
      <motion.section
        style={styles.highlightsSection}
        variants={fadeInVariant}
        initial="hidden"
        animate="visible"
      >
        <h3 style={styles.highlightsHeading}>Why Choose Us?</h3>
        <p style={styles.highlightsDescription}>
          At Ceylone Engineering, we stand out as a trusted name in the construction
          and ironworks industry. Here’s why:
        </p>
        <motion.div
          style={styles.highlights}
          variants={staggerContainer}
          initial="hidden"
          animate="visible"
        >
          {/* Expertise */}
          <motion.div
            className="highlight"
            style={styles.highlight}
            variants={fadeInVariant}
          >
            <div style={styles.imageWrapper}>
              <img
                src="../images/7.jpg" // Replace with a high-quality expertise image
                alt="Experience"
                style={styles.icon}
              />
            </div>
            <h4>Decades of Expertise</h4>
            <p>
              Over 15 years of delivering durable, high-quality ironworks for construction
              projects of all sizes.
            </p>
          </motion.div>

          {/* Precision */}
          <motion.div
            className="highlight"
            style={styles.highlight}
            variants={fadeInVariant}
          >
            <div style={styles.imageWrapper}>
              <img
                src="../images/7.jpg" // Replace with a high-quality precision image
                alt="Precision"
                style={styles.icon}
              />
            </div>
            <h4>Unmatched Precision</h4>
            <p>
              Cutting-edge tools and skilled craftsmen ensure every product fits seamlessly into
              your space.
            </p>
          </motion.div>

          {/* Innovation */}
          <motion.div
            className="highlight"
            style={styles.highlight}
            variants={fadeInVariant}
          >
            <div style={styles.imageWrapper}>
              <img
                src="../images/7.jpg" // Replace with a high-quality innovation image
                alt="Innovation"
                style={styles.icon}
              />
            </div>
            <h4>Modern Innovation</h4>
            <p>
              Innovative designs and techniques tailored to meet the evolving needs of modern
              construction.
            </p>
          </motion.div>

          {/* Durability */}
          <motion.div
            className="highlight"
            style={styles.highlight}
            variants={fadeInVariant}
          >
            <div style={styles.imageWrapper}>
              <img
                src="../images/7.jpg" // Replace with a high-quality durability image
                alt="Durability"
                style={styles.icon}
              />
            </div>
            <h4>Built to Last</h4>
            <p>
              Our ironworks are designed to withstand the test of time, even in the toughest
              environments.
            </p>
          </motion.div>
        </motion.div>
      </motion.section>
    </div>
  );
};

const styles = {
  container: {
    fontFamily: "'Roboto', sans-serif",
    color: "#333",
    lineHeight: "1.6",
    backgroundColor: "#f7f7f7",
  },
  aboutSection: {
    padding: "40px 20px",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#e2e2e2",
    borderRadius: "10px",
    boxShadow: "0 8px 16px rgba(0, 0, 0, 0.1)",
  },
  aboutContent: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    maxWidth: "1200px",
    width: "100%",
    gap: "20px",
  },
  imageWrapper: {
    borderRadius: "15px",
    overflow: "hidden",
    boxShadow: "0px 8px 16px rgba(0, 0, 0, 0.2)",
    transform: "scale(1)",
    transition: "transform 0.3s ease-in-out",
  },
  image: {
    width: "100%",
    height: "auto",
    display: "block",
  },
  textContent: {
    flex: 1,
    textAlign: "left",
  },
  highlightsSection: {
    padding: "40px 20px",
    backgroundColor: "#f0f0f0",
    textAlign: "center",
  },
  highlights: {
    display: "flex",
    justifyContent: "center",
    flexWrap: "wrap",
    gap: "20px",
  },
  highlight: {
    flex: "1 1 calc(25% - 20px)",
    minWidth: "200px",
    maxWidth: "300px",
    padding: "20px",
    backgroundColor: "#ffffff",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    textAlign: "center",
    transition: "transform 0.3s ease",
  },
  icon: {
    width: "100%",
    borderRadius: "10px",
    height: "auto",
    marginBottom: "15px",
    boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)",
  },
};

export default AboutUs;
