import React, { useEffect } from 'react';
// import './assets/css/AboutUs.css'; // Import custom CSS for AboutUs styling
import '.'
const AboutUs = () => {
  
  useEffect(() => {
    // JavaScript Animation on Scroll - Fade In Effect
    const handleScroll = () => {
      const aboutSection = document.querySelector('.about-us');
      const aboutContent = document.querySelector('.about-content');
      const aboutImage = document.querySelector('.about-image img');
      
      const scrollPosition = window.scrollY + window.innerHeight;
      const sectionPosition = aboutSection.offsetTop + aboutSection.offsetHeight;

      if (scrollPosition > sectionPosition) {
        aboutContent.classList.add('fade-in');
        aboutImage.classList.add('image-zoom');
      } else {
        aboutContent.classList.remove('fade-in');
        aboutImage.classList.remove('image-zoom');
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <section className="about-us">
      <div className="about-container">
        <h2>About Us</h2>
        <div className="about-content">
          <div className="about-text">
            <h3>Expert Ironwork Solutions for Every Project</h3>
            <p>
              We are a trusted leader in the ironwork construction industry, specializing in the fabrication and installation of high-quality, durable metal structures. Our skilled team delivers exceptional craftsmanship in every project, ensuring that we meet and exceed the expectations of our clients.
            </p>
            <p>
              From residential iron fencing to large commercial steel structures, we provide expert solutions for all types of ironwork needs. We take pride in our work, using the highest quality materials and the latest techniques to ensure durability and aesthetic appeal.
            </p>
          </div>
          <div className="about-image">
            <img src="https://via.placeholder.com/500x300?text=Iron+Work+Construction" alt="Iron Work Construction" />
          </div>
        </div>
      </div>
      <div className="mission-vision">
        <div className="mission">
          <h4>Our Mission</h4>
          <p>
            To provide top-notch ironwork construction services that meet the needs of residential, commercial, and industrial sectors while maintaining the highest standards of quality, safety, and efficiency.
          </p>
        </div>
        <div className="vision">
          <h4>Our Vision</h4>
          <p>
            To be the most trusted and innovative ironwork construction company, recognized for our craftsmanship, dedication, and integrity.
          </p>
        </div>
      </div>
    </section>
  );
};

export default AboutUs;
