import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../assets/css/ImageUploader.css'; // CSS import

const ImageUploader = () => {
  const [image, setImage] = useState(null);
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [images, setImages] = useState([]); // State to store images fetched from the server
  const navigate = useNavigate();

  // Check if the user is logged in
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('You need to log in first.');
      navigate('/Loging');
    }

    // Fetch images from server on component mount
    const fetchImages = async () => {
      try {
        const response = await axios.get('http://localhost:3001/images');
        setImages(response.data); // Store images in the state
      } catch (error) {
        console.error('Error fetching images:', error);
      }
    };

    fetchImages();
  }, [navigate]);

  const handleFileChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!image || !name || !description) {
      alert('Please fill all fields and select an image to upload!');
      return;
    }

    const formData = new FormData();
    formData.append('image', image);
    formData.append('name', name);
    formData.append('description', description);

    try {
      const response = await axios.post('http://localhost:3001/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      console.log('Upload success:', response.data);
      // Fetch updated list of images after upload
      const newImages = await axios.get('http://localhost:3001/images');
      setImages(newImages.data);
    } catch (error) {
      console.error('Error uploading image:', error);
    }
  };

  const handleDelete = async (imageId) => {
    try {
      await axios.delete(`http://localhost:3001/image/${imageId}`);
      console.log('Image deleted');
      // Remove the deleted image from the state
      setImages((prevImages) => prevImages.filter((img) => img._id !== imageId));
    } catch (error) {
      console.error('Error deleting image:', error);
    }
  };

  return (
    <div>
      <br />
      <br />
      <br />
      <br />
      <br />
      <input type="file" onChange={handleFileChange} />
      <input
        type="text"
        placeholder="Enter image name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <input
        type="text"
        placeholder="Enter image description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <button onClick={handleUpload}>Upload Image</button>

      <div className="image-gallery">
        {images.map((image) => (
          <div key={image._id} className="image-item">
            <img
              src={`http://localhost:3001${image.filepath}`} // Ensure this matches the backend path
              alt={`uploaded img ${image._id}`}
              style={{ width: '100px', height: '100px' }}
            />
            <h3>{image.name}</h3>
            <p>{image.description}</p>
            <button onClick={() => handleDelete(image._id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ImageUploader;
