import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });

  const navigate = useNavigate();

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleLogin = (e) => {
    e.preventDefault();

    const { email, password } = formData;

    axios.post('http://localhost:3001/login', { email, password })
      .then((result) => {
        if (result.status === 200) {
          console.log('Login successful', result.data);
          localStorage.setItem('token', result.data.token); // Store token in localStorage
          navigate('/dashboard'); // Redirect to a protected route after login
        }
      })
      .catch((err) => {
        if (err.response && err.response.status === 400) {
          alert('User not found');
        } else if (err.response && err.response.status === 401) {
          alert('Invalid password');
        } else {
          console.log('Login error', err);
        }
      });
  };

  const formStyles = {
    container: {
      width: '100%',
      maxWidth: '400px',
      margin: '50px auto',
      padding: '20px',
      border: '1px solid #ccc',
      borderRadius: '10px',
      backgroundColor: '#f9f9f9',
      boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)',
    },
    heading: {
      textAlign: 'center',
      marginBottom: '20px',
      color: '#333',
    },
    label: {
      display: 'block',
      marginBottom: '5px',
      color: '#555',
    },
    input: {
      width: '100%',
      padding: '10px',
      marginBottom: '15px',
      borderRadius: '5px',
      border: '1px solid #ccc',
      fontSize: '16px',
    },
    button: {
      width: '100%',
      padding: '10px',
      backgroundColor: '#00bcd4',
      color: 'white',
      fontSize: '16px',
      border: 'none',
      borderRadius: '5px',
      cursor: 'pointer',
    },
  };

  return (
    <div style={formStyles.container}>
      <h2 style={formStyles.heading}>Login</h2>
      <form onSubmit={handleLogin}>
        <label style={formStyles.label} htmlFor="email">
          Email
        </label>
        <input
          style={formStyles.input}
          type="email"
          id="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          required
        />

        <label style={formStyles.label} htmlFor="password">
          Password
        </label>
        <input
          style={formStyles.input}
          type="password"
          id="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          required
        />

        <button style={formStyles.button} type="submit">
          Login
        </button>
      </form>
    </div>
  );
};

export default Login;
