import React from "react";
import { GiMetalBar, GiMechanicGarage, GiFactory, GiShield } from "react-icons/gi";
import { motion } from "framer-motion";
import "../assets/css/ServicePage.css";

const services = [
  {
    title: "Custom Metal Fabrication",
    description: "We provide high-quality custom metal fabrication tailored to your specific needs.",
    icon: <GiMetalBar size={40} className="icon" />,
  },
  {
    title: "Repair and Maintenance",
    description: "Expert repair and maintenance services for all types of ironworks.",
    icon: <GiMechanicGarage size={40} className="icon" />,
  },
  {
    title: "Structural Installations",
    description: "Reliable and durable iron structure installations for residential and commercial projects.",
    icon: <GiFactory size={40} className="icon" />,
  },
  {
    title: "Safety and Security Solutions",
    description: "Iron gates, grills, and fences designed for maximum safety and aesthetic appeal.",
    icon: <GiShield size={40} className="icon" />,
  },
];

export default function OurServices() {
  return (
    <div
      className="container"
      style={{
        background: "url('/assets/images/background.jpg') no-repeat center center/cover",
        backgroundColor: "rgba(0, 0, 0, 0.7)",
        backgroundBlendMode: "overlay",
        color: "#fff",
      }}
    >
      <h1 className="title">Our Services</h1>
      <div className="grid">
        {services.map((service, index) => (
          <motion.div
            key={index}
            className="card"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.5, delay: index * 0.2 }}
          >
            <div className="icon-details-container">
              <div className="icon-container">{service.icon}</div>
              <div className="card-content">
                <h2 className="card-title">{service.title}</h2>
                <p className="card-description">{service.description}</p>
                <button className="card-button">Learn More</button>
              </div>
            </div>
          </motion.div>
        ))}
      </div>
    </div>
  );
}
