import React from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { FaTools, FaWrench, FaBuilding, FaShieldAlt } from "react-icons/fa";

const services = [
  {
    title: "Custom Metal Fabrication",
    description: "We provide high-quality custom metal fabrication tailored to your specific needs.",
    icon: <FaTools size={32} className="text-blue-500" />,
  },
  {
    title: "Repair and Maintenance",
    description: "Expert repair and maintenance services for all types of ironworks.",
    icon: <FaWrench size={32} className="text-green-500" />,
  },
  {
    title: "Structural Installations",
    description: "Reliable and durable iron structure installations for residential and commercial projects.",
    icon: <FaBuilding size={32} className="text-red-500" />,
  },
  {
    title: "Safety and Security Solutions",
    description: "Iron gates, grills, and fences designed for maximum safety and aesthetic appeal.",
    icon: <FaShieldAlt size={32} className="text-yellow-500" />,
  },
];

export default function OurServices() {
  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold text-center mb-8">Our Services</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {services.map((service, index) => (
          <Card key={index} className="shadow-lg hover:shadow-2xl transition-shadow duration-300">
            <CardContent>
              <div className="flex items-center justify-center mb-4">{service.icon}</div>
              <h2 className="text-xl font-semibold mb-2 text-center">{service.title}</h2>
              <p className="text-center text-gray-600">{service.description}</p>
              <div className="text-center mt-4">
                <Button className="bg-blue-500 text-white hover:bg-blue-600">
                  Learn More
                </Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
}
