import React from "react";
import { motion } from "framer-motion";
import '../components/Header.jsx';

const AboutUs = () => {
  return (
    <div className="about-us-page" style={styles.container}>
      {/* About Section */}
      <section style={styles.aboutSection}>
        <div style={styles.aboutContent}>
          {/* Image */}
          <motion.div
            style={styles.imageContainer}
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{
              duration: 1.5,
              ease: "easeInOut",
            }}
          >
            <img
              src="/images/1.jpg" // Replace with construction-related image
              alt="Ceylone Engineering Team"
              style={styles.image}
            />
          </motion.div>
          {/* Text Content */}
          <motion.div
            style={styles.textContent}
            initial={{ x: -100, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{
              duration: 1.5,
              ease: "easeInOut",
              delay: 0.2,
            }}
          >
            <h2 style={styles.heading}>Who We Are</h2>
            <p style={styles.paragraph}>
              At Ceylone Engineering, we’ve been crafting top-notch ironworks for
              over 15 years. From custom gates to intricate railings, we combine
              tradition, technology, and passion to deliver excellence. Our mission
              is to enhance every space with durable, elegant, and bespoke iron
              creations.
            </p>
          </motion.div>
        </div>
      </section>

      {/* Why Choose Us Section */}
      <section style={styles.highlightsSection}>
        <h3 style={styles.highlightsHeading}>Why Choose Us?</h3>
        <p style={styles.highlightsDescription}>
          At Ceylone Engineering, we stand out as a trusted name in the construction
          and ironworks industry. Here’s why:
        </p>
        <div style={styles.highlights}>
          {/* Expertise */}
          <motion.div
            className="highlight"
            style={styles.highlight}
            initial={{ y: 100, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 1, ease: "easeOut", delay: 0.1 }}
          >
            <img
              src="../images/7.jpg" // Replace with an appropriate construction-related icon
              alt="Experience"
              style={styles.icon}
            />
            <h4>Decades of Expertise</h4>
            <p>
              Over 15 years of delivering durable, high-quality ironworks for construction
              projects of all sizes.
            </p>
          </motion.div>

          {/* Precision */}
          <motion.div
            className="highlight"
            style={styles.highlight}
            initial={{ y: 100, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 1, ease: "easeOut", delay: 0.2 }}
          >
            <img
              src="../images/7.jpg" // Replace with a precision-related icon
              alt="Precision"
              style={styles.icon}
            />
            <h4>Unmatched Precision</h4>
            <p>
              Cutting-edge tools and skilled craftsmen ensure every product fits seamlessly into
              your space.
            </p>
          </motion.div>

          {/* Innovation */}
          <motion.div
            className="highlight"
            style={styles.highlight}
            initial={{ y: 100, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 1, ease: "easeOut", delay: 0.3 }}
          >
            <img
              src="../images/7.jpg" // Replace with an innovation-related icon
              alt="Innovation"
              style={styles.icon}
            />
            <h4>Modern Innovation</h4>
            <p>
              Innovative designs and techniques tailored to meet the evolving needs of modern
              construction.
            </p>
          </motion.div>

          {/* Durability */}
          <motion.div
            className="highlight"
            style={styles.highlight}
            initial={{ y: 100, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 1, ease: "easeOut", delay: 0.4 }}
          >
            <img
              src="../images/7.jpg" // Replace with a durability-related icon
              alt="Durability"
              style={styles.icon}
            />
            <h4>Built to Last</h4>
            <p>
              Our ironworks are designed to withstand the test of time, even in the toughest
              environments.
            </p>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

const styles = {
  container: {
    fontFamily: "'Roboto', sans-serif",
    color: "#333",
    lineHeight: "1.6",
    backgroundColor: "#f7f7f7",
  },
  aboutSection: {
    padding: "40px 20px",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#e2e2e2",
    borderRadius: "10px",
    boxShadow: "0 8px 16px rgba(0, 0, 0, 0.1)",
  },
  aboutContent: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    maxWidth: "1200px",
    width: "100%",
    gap: "20px",
  },
  imageContainer: {
    flex: 1,
    display: "flex",
    justifyContent: "center",
  },
  textContent: {
    flex: 1,
    textAlign: "left",
  },
  image: {
    width: "60%",
    maxWidth: "500px",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
  },
  heading: {
    fontSize: "2.5rem",
    fontWeight: "bold",
    marginBottom: "1rem",
    color: "#f0a500",
  },
  paragraph: {
    fontSize: "1.1rem",
    marginBottom: "1.5rem",
    color: "#444",
  },
  highlightsSection: {
    padding: "40px 20px",
    backgroundColor: "#f0f0f0",
    textAlign: "center",
  },
  highlightsHeading: {
    fontSize: "2.5rem",
    fontWeight: "bold",
    color: "#f0a500",
    marginBottom: "20px",
  },
  highlightsDescription: {
    fontSize: "1.2rem",
    color: "#555",
    marginBottom: "30px",
  },
  highlights: {
    display: "flex",
    justifyContent: "center",
    flexWrap: "wrap",
    gap: "20px",
  },
  highlight: {
    flex: "1 1 calc(25% - 20px)",
    minWidth: "200px",
    maxWidth: "300px",
    padding: "20px",
    backgroundColor: "#ffffff",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    textAlign: "center",
    transition: "transform 0.3s ease",
  },
  icon: {
    width: "50px",
    height: "50px",
    marginBottom: "15px",
  },
};

export default AboutUs;
