import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faLaptop, faBrush, faShippingFast } from '@fortawesome/free-solid-svg-icons';

const services = [
  {
    id: 1,
    icon: faLaptop,
    title: "Web Development",
    description: "We create responsive and modern websites for businesses and individuals. Our websites are optimized for all devices."
  },
  {
    id: 2,
    icon: faBrush,
    title: "UI/UX Design",
    description: "Our designers focus on creating intuitive user interfaces and experiences that enhance user engagement."
  },
  {
    id: 3,
    icon: faShippingFast,
    title: "Fast Delivery",
    description: "We ensure that your project is delivered on time, ensuring high-quality results."
  }
];

const ServicePage = () => {
  return (
    <div className="service-container">
      <h1 className="service-title">Our Services</h1>
      <div className="service-list">
        {services.map(service => (
          <div key={service.id} className="service-item">
            <FontAwesomeIcon icon={service.icon} size="3x" />
            <h2>{service.title}</h2>
            <p>{service.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ServicePage;
