import React from "react";
import '../components/Header.jsx';

const AboutUs = () => {
  return (
    <div className="about-us-page" style={styles.container}>
      {/* About Section */}
      <section style={styles.aboutSection}>
        <div style={styles.aboutContent}>
          {/* Image */}
          <div style={styles.imageContainer}>
            <img
              src="/images/ironworks-team.jpg"
              alt="Ceylone Engineering Team"
              style={styles.image}
            />
          </div>
          {/* Text Content */}
          <div style={styles.textContent}>
            <h2 style={styles.heading}>Who We Are</h2>
            <p style={styles.paragraph}>
              At Ceylone Engineering, weâ€™ve been crafting top-notch ironworks for
              over 15 years. From custom gates to intricate railings, we combine
              tradition, technology, and passion to deliver excellence. Our mission
              is to enhance every space with durable, elegant, and bespoke iron
              creations.
            </p>
          </div>
        </div>
      </section>
    </div>
  );
};

const styles = {
  container: {
    fontFamily: "'Roboto', sans-serif",
    color: "#333",
    lineHeight: "1.6",
  },
  aboutSection: {
    padding: "40px 20px",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  aboutContent: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    maxWidth: "1200px",
    width: "100%",
    gap: "20px",
  },
  imageContainer: {
    flex: 1,
    display: "flex",
    justifyContent: "center",
  },
  textContent: {
    flex: 1,
    textAlign: "left",
  },
  image: {
    width: "100%",
    maxWidth: "500px",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  },
  heading: {
    fontSize: "2rem",
    marginBottom: "1rem",
  },
  paragraph: {
    fontSize: "1rem",
    marginBottom: "1.5rem",
  },
};

export default AboutUs;
