import React from "react";
import { GiIronDoor, GiWeldingMask, GiMetalGate, GiArmorWelding } from "react-icons/gi"; // Updated icons
import { motion } from "framer-motion";
import "../assets/css/ServicePage.css";

const services = [
  {
    title: "Custom Metal Fabrication",
    description: "We provide high-quality custom metal fabrication tailored to your specific needs.",
    icon: <GiIronDoor size={60} className="icon" style={{ color: "#ffc107" }} />, // Increased icon size
  },
  {
    title: "Repair and Maintenance",
    description: "Expert repair and maintenance services for all types of ironworks.",
    icon: <GiWeldingMask size={60} className="icon" style={{ color: "#ffc107" }} />, // Increased icon size
  },
  {
    title: "Structural Installations",
    description: "Reliable and durable iron structure installations for residential and commercial projects.",
    icon: <GiMetalGate size={60} className="icon" style={{ color: "#ffc107" }} />, // Increased icon size
  },
  {
    title: "Safety and Security Solutions",
    description: "Iron gates, grills, and fences designed for maximum safety and aesthetic appeal.",
    icon: <GiArmorWelding size={60} className="icon" style={{ color: "#ffc107" }} />, // Increased icon size
  },
];

export default function OurServices() {
  return (
    <div
      className="container"
      style={{
        background: "url('/images/1.jpg') no-repeat center center/cover",
        backgroundColor: "rgba(255, 255, 255, 0.9)",
        backgroundBlendMode: "overlay",
        color: "#000",
        padding: "2rem",
        borderRadius: "8px",
      }}
    >
      <h1 className="title" style={{ textAlign: "center", color: "#000", marginBottom: "2rem", fontWeight: "bold" }}>
        Our Services
      </h1>
      <div className="grid" style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))", gap: "1.5rem" }}>
        {services.map((service, index) => (
          <motion.div
            key={index}
            className="card"
            style={{
              background: "#fff",
              borderRadius: "8px",
              boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
              padding: "1.5rem",
              textAlign: "center",
            }}
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.5, delay: index * 0.2 }}
          >
            <div className="icon-container" style={{ marginBottom: "1rem" }}>{service.icon}</div>
            <h2 className="card-title" style={{ marginBottom: "0.5rem", fontSize: "1.25rem", fontWeight: "bold" }}>{service.title}</h2>
            <p className="card-description" style={{ marginBottom: "1rem", fontSize: "0.9rem", color: "#555" }}>{service.description}</p>
            <button
              className="card-button"
              style={{
                background: "#ffc107",
                color: "#000",
                border: "none",
                padding: "0.5rem 1rem",
                borderRadius: "4px",
                cursor: "pointer",
              }}
            >
              Learn More
            </button>
          </motion.div>
        ))}
      </div>
    </div>
  );
}
