import React from 'react';
import CountUp from 'react-countup';
import '../assets/css/CounterBar.';

const ConstructionSection = () => {
  const counters = [
    { label: 'Projects', value: 598 },
    { label: 'Clients', value: 128 },
    { label: 'Success', value: 339 },
    { label: 'Awards', value: 109 },
  ];

  return (
    <div className="construction-section">
      <div className="left-content">
        <img
          src="https://via.placeholder.com/300x200"
          alt="Construction 1"
          className="image-top"
        />
        <img
          src="https://via.placeholder.com/300x200"
          alt="Construction 2"
          className="image-bottom"
        />
      </div>

      <div className="right-content">
        <h2># BEST CONSTRUCTION THEME</h2>
        <p>
          On the other hand, we denounce with righteous indignation and dislike
          men who are so beguiled and demoralized by the charms of pleasure.
        </p>
        <button className="btn-buy">BUY NOW</button>

        <div className="counter-bar">
          {counters.map((counter, index) => (
            <div key={index} className="counter-item">
              <h2>
                <CountUp start={0} end={counter.value} duration={3} />
              </h2>
              <p>{counter.label}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ConstructionSection;
