import React from "react";
import { motion } from "framer-motion";
import { FaHammer, FaTools, FaLightbulb, FaShieldAlt } from "react-icons/fa";

const AboutUs = () => {
  return (
    <div className="about-us-page">
      {/* About Section */}
      <section className="about-section">
        <div className="about-content">
          {/* Image */}
          <motion.div
            className="image-container"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1.5, ease: "easeInOut" }}
          >
            <img
              src="/images/1.jpg"
              alt="Ceylone Engineering Team"
              className="about-image"
            />
          </motion.div>

          {/* Text Content */}
          <motion.div
            className="text-content"
            initial={{ x: -100, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ duration: 1.5, ease: "easeInOut", delay: 0.2 }}
          >
            <h2 className="heading">Who We Are</h2>
            <p className="paragraph">
              At Ceylone Engineering, we’ve been crafting top-notch ironworks
              for over 15 years. From custom gates to intricate railings, we
              combine tradition, technology, and passion to deliver excellence.
              Our mission is to enhance every space with durable, elegant, and
              bespoke iron creations.
            </p>
            <p className="paragraph">
              Our commitment to craftsmanship and innovation sets us apart, and
              we pride ourselves on providing reliable solutions for
              construction projects of all sizes.
            </p>
          </motion.div>
        </div>
      </section>

      {/* Why Choose Us Section */}
      <section className="highlights-section">
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1.5, ease: "easeInOut" }}
        >
          <h3 className="highlights-heading">Why Choose Us?</h3>
          <p className="highlights-description">
            Here’s why our clients trust us for their construction and ironworks
            needs:
          </p>
        </motion.div>

        <div className="highlights">
          {[
            {
              icon: <FaHammer size={50} />,
              title: "Expertise",
              description: "15 years of delivering durable, high-quality ironworks.",
            },
            {
              icon: <FaTools size={50} />,
              title: "Precision",
              description: "Cutting-edge tools and skilled craftsmen ensure perfect results.",
            },
            {
              icon: <FaLightbulb size={50} />,
              title: "Innovation",
              description: "Modern designs and techniques tailored to today’s needs.",
            },
            {
              icon: <FaShieldAlt size={50} />,
              title: "Durability",
              description: "Built to withstand the toughest environments.",
            },
          ].map((item, index) => (
            <motion.div
              key={index}
              className="highlight"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.2 + index * 0.3, duration: 1.5 }}
            >
              <div className="icon-container">{item.icon}</div>
              <h4>{item.title}</h4>
              <p>{item.description}</p>
            </motion.div>
          ))}
        </div>
      </section>

      {/* CSS Styles */}
      <style>
        {`
          .about-us-page {
            font-family: "Roboto", sans-serif;
            color: #333;
            line-height: 1.6;
            background-color: #f7f7f7;
            padding: 20px;
          }

          /* About Section */
          .about-section {
            padding: 40px 20px;
            background-color: #e2e2e2;
            border-radius: 10px;
            box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1);
            margin-bottom: 40px;
          }

          .about-content {
            display: flex;
            flex-direction: row;
            align-items: center;
            justify-content: space-between;
            max-width: 1200px;
            margin: auto;
            gap: 20px;
          }

          .image-container {
            flex: 1;
            display: flex;
            justify-content: center;
          }

          .about-image {
            width: 100%;
            max-width: 500px;
            border-radius: 10px;
            box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
          }

          .text-content {
            flex: 1;
            text-align: left;
            padding: 20px;
          }

          .heading {
            font-size: 2.5rem;
            font-weight: bold;
            margin-bottom: 1rem;
            color: #f0a500;
          }

          .paragraph {
            font-size: 1.1rem;
            margin-bottom: 1.5rem;
            color: #444;
          }

          /* Highlights Section */
          .highlights-section {
            padding: 40px 20px;
            background-color: #ffffff;
            text-align: center;
            border-radius: 10px;
            box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1);
          }

          .highlights-heading {
            font-size: 2.2rem;
            font-weight: bold;
            color: #f0a500;
            margin-bottom: 1rem;
          }

          .highlights-description {
            font-size: 1.1rem;
            margin-bottom: 2rem;
            color: #333;
          }

          .highlights {
            display: flex;
            flex-wrap: wrap;
            justify-content: center;
            gap: 20px;
          }

          .highlight {
            flex: 1 1 calc(50% - 40px);
            max-width: 250px;
            padding: 20px;
            background-color: #fff;
            border-radius: 10px;
            box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
            text-align: center;
            transition: transform 0.3s ease;
          }

          .highlight:hover {
            transform: scale(1.05);
          }

          .icon-container {
            margin-bottom: 15px;
            color: #f0a500;
          }

          /* Responsive Design */
          @media (max-width: 768px) {
            .about-content {
              flex-direction: column;
              text-align: center;
            }
            .highlights {
              flex-direction: column;
              align-items: center;
            }
            .heading {
              font-size: 2rem;
            }
            .paragraph {
              font-size: 1rem;
            }
          }
        `}
      </style>
    </div>
  );
};

export default AboutUs;
