import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ImageGallery = () => {
  const [images, setImages] = useState([]);

  // Fetch images from the backend API
  useEffect(() => {
    const fetchImages = async () => {
      try {
        const response = await axios.get('http://localhost:3001/images');
        setImages(response.data);
      } catch (error) {
        console.error('Error fetching images:', error.message);
      }
    };
    fetchImages();
  }, []);

  // Function to delete an image
  const deleteImage = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/image/${id}`);
      setImages(images.filter((image) => image._id !== id)); // Remove deleted image from state
    } catch (error) {
      console.error('Error deleting image:', error.message);
    }
  };

  return (
    <div className="image-gallery">
      <h2>Image Gallery</h2>
      <div className="image-grid">
        {images.map((image) => (
          <div key={image._id} className="image-item">
            <img
              src={`http://localhost:3001${image.filepath}`} // Fix image path
              alt={image.filename}
              className="image"
            />
            <p>{image.filename}</p>
            <button onClick={() => deleteImage(image._id)} className="delete-btn">
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ImageGallery;
