// frontend/src/components/Loging.js
import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import '../assets/'

const Loging = () => {
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [otp, setOtp] = useState("");
  const [step, setStep] = useState(1); // 1: Login, 2: OTP Verification
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");
    setSuccessMessage("");

    try {
      const response = await axios.post("http://localhost:3001/api/login", formData);
      const { token, twoFactorEnabled } = response.data;

      if (!token) {
        throw new Error("No token received from the server");
      }

      if (twoFactorEnabled) {
        // If 2FA is enabled, move to OTP verification step
        await axios.post("http://localhost:3001/api/send-otp", { email: formData.email });
        setStep(2);
        setSuccessMessage("OTP sent to your email.");
      } else {
        // If 2FA is not enabled, proceed to login
        localStorage.setItem("token", token);
        setSuccessMessage("Login successful! Redirecting...");
        setTimeout(() => navigate("/AdminPanel"), 2000);
      }
    } catch (err) {
      setError(err.response?.data?.error || "Login failed. Please try again.");
    }
  };

  const handleVerifyOtp = async (e) => {
    e.preventDefault();
    setError("");
    setSuccessMessage("");

    try {
      const response = await axios.post("http://localhost:3001/api/verify-otp", {
        email: formData.email,
        otp,
      });

      if (response.data.message === "OTP verified successfully") {
        localStorage.setItem("token", response.data.token);
        setSuccessMessage("Login successful! Redirecting...");
        setTimeout(() => navigate("/AdminPanel"), 2000);
      } else {
        setError("Invalid OTP. Please try again.");
      }
    } catch (err) {
      setError(err.response?.data?.error || "OTP verification failed.");
    }
  };

  return (
    <div>
      <h2>Login</h2>
      {successMessage && <div>{successMessage}</div>}
      {error && <div>{error}</div>}

      {step === 1 && (
        <form onSubmit={handleLogin}>
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
          <label>Password</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
          <button type="submit">Login</button>
        </form>
      )}

      {step === 2 && (
        <form onSubmit={handleVerifyOtp}>
          <label>Enter OTP</label>
          <input
            type="text"
            name="otp"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
            required
          />
          <button type="submit">Verify OTP</button>
        </form>
      )}
    </div>
  );
};

export default Loging;
