import React from "react";
import { FaCog, FaTools, FaBuilding, FaLock } from "react-icons/fa"; // Modern icons
import { motion } from "framer-motion";
import "../assets/css/ServicePage.css";

const services = [
  {
    title: "Custom Metal Fabrication",
    description: "We deliver precise, high-quality custom metal fabrication services tailored to your requirements.",
    icon: <FaCog size={40} className="icon" />, // Modern cog icon
  },
  {
    title: "Repair and Maintenance",
    description: "Reliable repair and maintenance services to keep your ironworks in top-notch condition.",
    icon: <FaTools size={40} className="icon" />, // Tools icon
  },
  {
    title: "Structural Installations",
    description: "Expert installations of durable iron structures for homes, offices, and industries.",
    icon: <FaBuilding size={40} className="icon" />, // Building icon
  },
  {
    title: "Safety and Security Solutions",
    description: "Secure your premises with robust iron gates, fences, and grills built for protection.",
    icon: <FaLock size={40} className="icon" />, // Lock icon
  },
];

export default function OurServices() {
  return (
    <div
      className="container"
      style={{ background: "#000", color: "#fff", padding: "2rem" }}
    >
      <h1
        className="title"
        style={{
          fontSize: "3rem",
          textAlign: "center",
          marginBottom: "2rem",
          color: "#ffc107",
        }}
      >
        Our Services
      </h1>
      <div
        className="grid"
        style={{
          display: "grid",
          gap: "2rem",
          gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
        }}
      >
        {services.map((service, index) => (
          <motion.div
            key={index}
            className="card"
            style={{
              background: "#fff",
              borderRadius: "15px",
              padding: "1.5rem",
              textAlign: "center",
              boxShadow: "0 4px 8px rgba(0, 0, 0, 0.3)",
              transition: "transform 0.3s ease",
              color: "#000",
            }}
            whileHover={{ scale: 1.05 }}
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.5, delay: index * 0.2 }}
          >
            <div
              className="icon-container"
              style={{ marginBottom: "1rem", color: "#ffc107" }}
            >
              {service.icon}
            </div>
            <h2
              className="card-title"
              style={{ fontSize: "1.5rem", marginBottom: "1rem" }}
            >
              {service.title}
            </h2>
            <p
              className="card-description"
              style={{
                fontSize: "1rem",
                lineHeight: "1.5",
                color: "#000",
              }}
            >
              {service.description}
            </p>
            <button
              className="card-button"
              style={{
                marginTop: "1rem",
                padding: "0.75rem 1.5rem",
                background: "#ffc107",
                border: "none",
                borderRadius: "5px",
                color: "#000",
                fontWeight: "bold",
                cursor: "pointer",
                transition: "background 0.3s ease",
              }}
              onMouseOver={(e) => (e.target.style.background = "#e0a800")}
              onMouseOut={(e) => (e.target.style.background = "#ffc107")}
            >
              Learn More
            </button>
          </motion.div>
        ))}
      </div>
    </div>
  );
}
