import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

const Loging = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });
  const navigate = useNavigate();

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    console.log("Login Data Submitted:", formData);

    try {
      const response = await axios.post("http://localhost:3001/login", formData);
      if (response.status === 200) {
        console.log("Login Successful!");
        localStorage.setItem("userToken", response.data.token); // Save token if needed
        navigate("/home"); // Redirect to home page on successful login
      }
    } catch (err) {
      console.error(err);
      alert("Invalid email or password");
    }
  };

  const formStyles = {
    container: {
      width: "100%",
      maxWidth: "400px",
      margin: "50px auto",
      padding: "20px",
      border: "1px solid #ccc",
      borderRadius: "10px",
      backgroundColor: "#f9f9f9",
      boxShadow: "0 2px 5px rgba(0, 0, 0, 0.1)",
    },
    heading: {
      textAlign: "center",
      marginBottom: "20px",
      color: "#333",
    },
    label: {
      display: "block",
      marginBottom: "5px",
      color: "#555",
    },
    input: {
      width: "100%",
      padding: "10px",
      marginBottom: "15px",
      borderRadius: "5px",
      border: "1px solid #ccc",
      fontSize: "16px",
    },
    button: {
      width: "100%",
      padding: "10px",
      backgroundColor: "#00bcd4",
      color: "white",
      fontSize: "16px",
      border: "none",
      borderRadius: "5px",
      cursor: "pointer",
    },
    signupLink: {
      display: "block",
      marginTop: "15px",
      textAlign: "center",
      textDecoration: "none",
      color: "#00bcd4",
    },
  };

  return (
    <div style={formStyles.container}>
      <h2 style={formStyles.heading}>Login</h2>
      <form onSubmit={handleSubmit}>
        <label style={formStyles.label} htmlFor="email">
          Email
        </label>
        <input
          style={formStyles.input}
          type="email"
          id="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          required
        />

        <label style={formStyles.label} htmlFor="password">
          Password
        </label>
        <input
          style={formStyles.input}
          type="password"
          id="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          required
        />

        <button style={formStyles.button} type="submit">
          Login
        </button>
      </form>
      <Link to="/signup" style={formStyles.signupLink}>
        Don't have an account? Sign up here
      </Link>
    </div>
  );
};

export default Loging;
